function numberLineBuilder(e){let n=0;e.querySelectorAll("code").forEach(e=>{n++,this.createNumberLine(e,n)})}function createNumberLine(e,n){let r=document.createElement("span");r.innerHTML=n,r.classList.add("numberLine"),e.insertAdjacentElement("beforebegin",r)}document.querySelectorAll(".line-number-scroll").forEach(e=>{this.numberLineBuilder(e)}),document.querySelectorAll(".line-number-not-scroll").forEach(e=>{this.numberLineBuilder(e)});